import React, { Component } from "react";
import "./App.css";
import SearchBar from "./SearchBar";
import ArticleContainer from "./ArticleContainer";

const API_KEY = process.env.REACT_APP_API_KEY;

class App extends Component {
  state = {
    articles: [],
    userInput: "",
    sortBy: "",
  };

  componentDidMount = () => {
    fetch(
      `http://newsapi.org/v2/everything?q=bitcoin&from=2020-09-15&sortBy=publishedAt&apiKey=${API_KEY}`
    )
      .then((res) => res.json())
      .then((data) => this.setState({ articles: data.articles }));
  };

  handleChange = (e) => {
    let input = e.target.value;
    if (e.target.className === "UserInput") {
      this.setState({ userInput: input.toLowerCase() });
    } else {
      this.setState({ sortBy: input });
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();
    let endPoint = "http://newsapi.org/v2/everything";
    let apiKey = `&apiKey=${API_KEY}`;
    if (this.state.userInput) {
      endPoint += `?q=${this.state.userInput}`;
      //&from=2020-09-15 ?????????
      if (this.state.sortBy) {
        endPoint += `&sortBy=${this.state.sortBy}${apiKey}`;
      }
    }
  };

  render() {
    console.log(this.state.articles, "articles");
    return (
      <div className="App">
        <SearchBar
          userInput={this.state.userInput}
          sortBy={this.state.sortBy}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
        <ArticleContainer />
      </div>
    );
  }
}

export default App;
// Search results show the image, title, publish date, description and the url (as 'Read More' button) of the articles
