  //     .then(console.log(data, "articles"));
  // }

  // useEffect(() => {
  //   fetchData();
  // }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    useEffect(() => {
      fetch("https://jsonplaceholder.typicode.com/posts")
        .then((response) => response.json())
        .then((data) => {
          setPosts(data);
        }, []);
    });
  };

  return (
    <div className="App">
      <SearchBar
        userInput={userInput}
        sortBy={sortBy}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
      />
      {data.articles && <ArticleContainer articles={data.articles} />}
      <GoTop />
    </div>
  );
}

export default App;

