import React from "react";

const SearchBar = ({ userInput, sortBy, handleChange, handleSubmit, open }) => {
  return (
    <form className="SearchBar" onSubmit={handleSubmit}>
      <input
        className="UserInput"
        value={userInput}
        placeholder="Some search term"
        onChange={handleChange}
      />

      {/* <select className="UserSelect" value={sortBy} onChange={handleChange}>
        <option value="default">Sort Articles</option>
        <option value="publishedAt">Date</option>
        <option value="relevancy">Relevance</option>
        <option value="popularity">Popularity</option>
        <option value="none">None</option>
      </select> */}

      <div className="container">
        <button
          type="button"
          className="button"
          onClick={this.handleButtonClick}
        >
          Sort Articles
        </button>
        {open && (
          <div className="dropdown">
            <ul>
              <li value="publishedAt">Date</li>
              <li value="relevancy">Relevance</li>
              <li value="popularity">Popularity</li>
              <li value="none">None</li>
            </ul>
          </div>
        )}
      </div>

      <input className="SubmitButton" type="submit" value="Submit" />
    </form>
  );
};

export default SearchBar;
