import React, { Component } from "react";
import "./App.css";
import SearchBar from "./SearchBar";
import ArticleContainer from "./ArticleContainer";

const API_KEY = process.env.REACT_APP_API_KEY;

class App extends Component {
  state = {
    articles: [],
  };

  componentDidMount = () => {
    fetch(
      `http://newsapi.org/v2/everything?q=bitcoin&from=2020-09-15&sortBy=publishedAt&apiKey=${API_KEY}`
    )
      .then((res) => res.json())
      .then((data) => this.setState({ articles: data.articles }));
  };

  render() {
    console.log(this.state.articles, "articles");
    return <div className="App">hello</div>;
  }
}

export default App;
// Search results show the image, title, publish date, description and the url (as 'Read More' button) of the articles
