import React, { Component } from "react";
import "./App.css";
import SearchBar from "./SearchBar";
import ArticleContainer from "./ArticleContainer";

const API_KEY = process.env.REACT_APP_API_KEY;

class App extends Component {
  state = {
    articles: [],
    userInput: "",
    sortBy: "",
  };

  handleChange = (e) => {
    let input = e.target.value;
    if (e.target.className === "UserSelect") {
      input === "none"
        ? this.setState({ sortBy: "" })
        : this.setState({ sortBy: input });
    } else {
      this.setState({ userInput: input.toLowerCase() });
    }
  };

  handleSubmit = (e) => {
    e.preventDefault();
    let endPoint = "http://newsapi.org/v2/everything";
    let userInput = this.state.userInput
      ? `?q=${this.state.userInput}`
      : `?q=""`;
    let sortBy = this.state.sortBy
      ? `&sortBy=${this.state.sortBy}`
      : `&sortBy=""`;
    let apiKey = `&apiKey=${API_KEY}`;

    fetch(endPoint + userInput + sortBy + apiKey)
      .then((res) => res.json())
      .then((data) => this.setState({ articles: data.articles }));
  };

  render() {
    // console.log(this.state.sortBy, "sortBy");
    return (
      <div className="App">
        <SearchBar
          userInput={this.state.userInput}
          sortBy={this.state.sortBy}
          handleChange={this.handleChange}
          handleSubmit={this.handleSubmit}
        />
        <ArticleContainer articles={this.state.articles} />
      </div>
    );
  }
}

export default App;
