import React from "react";

const SearchBar = ({ userInput, sortBy, handleChange, handleSubmit }) => {
  return (
    <form onSubmit={handleSubmit}>
      <div>
        <input
          className="UserInput"
          value={userInput}
          placeholder="Some search term"
          onChange={handleChange}
        />
      </div>
      <div>
        <DropdownButton
          id="dropdown-basic-button"
          value={sortBy}
          title="Sort Articles"
          onChange={handleChange}
        >
          <Dropdown.Item href="#/action-1">Date</Dropdown.Item>
          <Dropdown.Item href="#/action-2">Relevance</Dropdown.Item>
          <Dropdown.Item href="#/action-3">Popularity</Dropdown.Item>
          <Dropdown.Item href="#/action-3">None</Dropdown.Item>
        </DropdownButton>
        {/* <select value={sortBy} onChange={handleChange}>
          <option value="default">Sort Articles</option>
          <option value="publishedAt">Date</option>
          <option value="relevancy">Relevance</option>
          <option value="popularity">Popularity</option>
          <option value="none">None</option>
        </select> */}
      </div>
      <div>
        <input className="SubmitButton" type="submit" value="Submit" />
      </div>
    </form>
  );
};

export default SearchBar;
