import React, { useState, useEffect } from "react";
import "./css/style.scss";
import SearchBar, { text } from "./components/SearchBar";
import ArticleContainer from "./components/ArticleContainer";
import GoTop from "./components/GoTop";
import ReadLaterContainer from "./components/ReadLaterContainer";
import ReadLater from "./components/ReadLater";

const API_KEY = process.env.REACT_APP_API_KEY;

const App = () => {
  const [data, setData] = useState({ articles: [] });
  const [userInput, setUserInput] = useState("");
  const [sortBy, setSortBy] = useState("");
  const [ReadLaters, setReadLaters] = useState([]);

  const handleChange = (e) => {
    const input = e.target.value;
    switch (e.target.className) {
      case "UserSelect":
        setSortBy(input);
        break;
      case "UserInput":
        const trimmedInput = input.replace(/^\s+|\s+$|\s+(?=\s)/g, "");
        setUserInput(trimmedInput.toLowerCase());
        break;
      default:
        console.log("User input not found");
    }
  };

  const endPoint = "http://newsapi.org/v2/everything";

  async function fetchData() {
    const userInputVal = `?q="${userInput}"`;
    const sortByVal = sortBy ? `&sortBy=${sortBy}` : `&sortBy=""`;
    const language = "&language=en";
    const apiKey = `&apiKey=${API_KEY}`;
    const url = endPoint + userInputVal + sortByVal + language + apiKey;

    await fetch(url)
      .then((res) => res.json())
      .then((data) => setData({ articles: data.articles }))
      .catch((error) => console.log("Request failed", error));
  }

  useEffect(() => {
    fetchData();
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    fetchData();
  };

  const getArticles = () => {
    fetch("http://localhost:3000/articles", {
      method: "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(article),
    })
      .then((res) => res.json)
      .then((newData) => console.log(newData));
  };

  useEffect((article) => {
    getArticles();
  }, []);

  const handleArticles = (article) => {
    // setReadLaters([...ReadLaters, article]);
  };

  const handleRemove = (article) => {
    let filteredArticles = [...ReadLaters].filter(
      (art) => art.id !== article.id
    );
    setReadLaters([filteredArticles]);
  };

  console.log(ReadLaters, "read");

  return (
    <div className="App">
      <header>
        <SearchBar handleChange={handleChange} handleSubmit={handleSubmit} />
      </header>
      <main>
        <ReadLaterContainer articles={ReadLaters} handleRemove={handleRemove} />
        <ArticleContainer
          articles={data.articles}
          handleArticles={handleArticles}
        />
        <GoTop />
      </main>
    </div>
  );
};

export default App;
